mapper.select:
SELECT * FROM events
WHERE organizerId = #{organizerId}

    AND title LIKE CONCAT('%', #{title}, '%')


    AND status = #{status}

SELECT COUNT(*) FROM events
WHERE organizerId = #{organizerId}

    AND title LIKE CONCAT('%', #{title}, '%')


    AND status = #{status}

mapper.update:
UPDATE events
SET organizerId = #{organizerId}, title = #{title}, description = #{description}, location = #{location}, pointsAwarded = #{pointsAwarded}, startDate = #{startDate}, endDate = #{endDate}
WHERE id = #{id};
